mapper.select:
select count(*)
from member
where id=#{id}

select count(*)
from member
where email=#{email}

select id, name, email
from member
where id=#{id} and pwd=#{pwd}

mapper.update:
update member
set pwd=#{pwd}
where email=#{email}

mapper.select:
select id
from member
where name=#{name} and email=#{email}
